document.addEventListener("DOMContentLoaded",function(){let t=document.getElementById("cadastroBibliotecaForm");t&&t.addEventListener("submit",function(e){e.preventDefault();e=new FormData(t);fetch("/cadastro/nova",{method:"POST",body:e}).then(e=>e.json()).then(e=>{e.success?alert("Biblioteca cadastrada com sucesso"):alert(e.error||"Erro ao adicionar biblioteca.")}).catch(e=>{console.error("Erro:"+e)})})});