document.addEventListener("DOMContentLoaded",function(){document.querySelectorAll(".btn-bibliogo").forEach(e=>{e.addEventListener("click",function(){var e=this.getAttribute("data-id");e=e,confirm("Tem certeza que deseja devolver este aluguel?")&&fetch("/api/leitor/devolver/"+e,{method:"POST",headers:{"Content-Type":"application/json","X-CSRF-TOKEN":document.querySelector('input[name="_csrf_token"]')?.value}}).then(e=>e.json()).then(e=>{e.success?(alert("Aluguel devolvido com sucesso!"),location.reload()):alert("Erro ao devolver o aluguel. Tente novamente.")}).catch(e=>console.error("Erro:",e))})})});